(42, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2026-01-10 09:13:48', NULL, 0, 'plain', '', 0, 0, 'info'),
(43, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2026-01-10 09:13:48', NULL, 0, 'plain', '', 0, 0, 'info'),
(44, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2026-01-10 09:13:48', NULL, 0, 'plain', '', 0, 0, 'info'),
(45, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2026-01-10 09:13:48', NULL, 0, 'plain', '', 0, 0, 'info'),
(46, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2026-01-10 09:13:48', NULL, 0, 'plain', '', 0, 0, 'info'),
(47, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and wed love to make things right!', '{}', 'pending', 'woocommerce.com', '2026-01-10 09:13:48', NULL, 0, 'plain', '', 0, 0, 'info'),
(48, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2026-01-10 09:13:48', NULL, 0, 'plain', '', 0, 0, 'info'),
(49, 'sirt-woo-2024-11-xss-admin', 'update', 'en_US', 'An important update is required for WooCommerce', 'This update addresses a security vulnerability that could allow unauthorized access to administrative privileges on your site. <a href=\"https://developer.woocommerce.com/2024/12/03/woocommerce-9-4-3-and-woocommerce-9-3-4-available-now/\">Learn more</a>.\r\n<br><br>\r\n<strong>Please ensure your site is updated to the latest version to stay secure.</strong>', '{}', 'pending', 'woocommerce.com', '2026-01-10 09:13:48', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'paypal-braintree-sdk-depreciation-2025-02-20', 'update', 'en_US', 'Update required for your PayPal Braintree extension', 'To continue securely accepting payments, you must update your PayPal Braintree extension to version 3.2.4 or newer before June 30, 2025. Your current extension version will not support payment processing after this date due to an SSL certificate update.', '{}', 'pending', 'woocommerce.com', '2026-01-10 09:13:48', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'uk-vat-notice-2025-04-30', 'warning', 'en_US', 'UK VAT numbers are no longer supported', 'Due to recent HM Revenue &amp; Customs (HMRC) VAT API changes, your EU VAT Number for WooCommerce extension can no longer validate UK VAT numbers. HMRC now requires merchants to register an API key, which this extension does not currently support. We recommend EU/UK VAT Validation Manager for WooCommerce as a replacement', '{}', 'pending', 'woocommerce.com', '2026-01-10 09:13:48', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'paypal-braintree-sdk-depreciation-2025-05-30', 'update', 'en_US', 'Action required: Update the PayPal Braintree extension', 'Your PayPal Braintree extension is using an outdated SDK and will stop processing payments after June 30, 2025, due to a root SSL certificate expiration. To avoid service disruption, update to version 3.2.4 or newer immediately.', '{}', 'pending', 'woocommerce.com', '2026-01-10 09:13:48', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'paypal-carding-recaptcha-dec-25', 'update', 'en_US', 'Activate PayPal fraud management', 'PayPal detected increased suspicious card activity in market. Please enable fraud protection in your PayPal Payment settings by enabling CAPTCHA for PayPal Payments.', '{}', 'pending', 'woocommerce.com', '2026-01-10 09:13:48', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'downloads-sub-Jan2026-RIN', 'marketing', 'en_US', 'WooCommerce Subscription Downloads is now part of WooCommerce Subscriptions', 'WooCommerce Subscriptions now allows you to include simple and variable downloadable products with your subscriptions. Streamline your subscription downloads by updating to the latest version of WooCommerce Subscriptions today.', '{}', 'pending', 'woocommerce.com', '2026-01-10 09:13:48', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'Cch ty chnh danh mc sn phm ca bn', 'Bn mun danh mc sn phm v hnh nh ca mnh trng tht p v ph hp vi thng hiu ca bn. Hng dn ny s cung cp cho bn tt c cc mo cn thit  lm cho sn phm ca bn trng tuyt vi trong ca hng.', '{}', 'unactioned', 'woocommerce-admin', '2026-01-12 13:45:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'wc-admin-launch-checklist', 'info', 'en_US', 'Bn  sn sng  ra mt ca hng ca mnh?', ' m bo bn khng bao gi c cm gic chm m \"mnh  qun iu g\", chng ti  tp hp danh sch kim tra trc khi ra mt cn thit.', '{}', 'unactioned', 'woocommerce-admin', '2026-01-12 13:45:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'wc-admin-mobile-app', 'info', 'en_US', 'Ci t ng dng di ng Woo', 'Ci t ng dng di ng WooCommerce  qun l n t hng, nhn thng bo bn hng v xem cc s liu chnh - mi lc mi ni.', '{}', 'unactioned', 'woocommerce-admin', '2026-01-12 13:45:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'paypal-carding-recaptcha-jan-26', 'update', 'en_US', 'Fraud protection is now required  enable today', 'Card networks like Visa and Mastercard now require fraud prevention controls, and non-compliance may result in fines and processing restrictions. Please enable reCAPTCHA in your PayPal Payments settings to help protect your store and maintain compliance.', '{}', 'pending', 'woocommerce.com', '2026-01-13 13:46:46', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'USPS API Migration_RIN_Jan2026', 'marketing', 'en_US', 'Upgrade to USPS Shipping REST API by January 25', 'USPS is retiring the legacy Web Tools API on January 25, 2026. If youre using the USPS Shipping Method extension with the legacy API, live USPS shipping rates will longer be shown at checkout. Migrate to the REST API now to avoid disruption.', '{}', 'pending', 'woocommerce.com', '2026-01-14 13:46:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'existing_square_bitcoin_promotion_q1_2026', 'marketing', 'en_US', 'Take bitcoin payments in Square with 0% fees in 2026', 'Your Square POS can now accept in-person bitcoin payments, with near-instant settlement and no chargebacks. Enable it today and pay zero fees through 2026. T&amp;Cs apply.', '{}', 'pending', 'woocommerce.com', '2026-01-23 17:04:43', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'prospects_square_bitcoin_promotion_q1_2026', 'marketing', 'en_US', 'Take bitcoin payments in Square with 0% fees in 2026', 'Accept in-person <a href=\"https://woocommerce.com/posts/square-bitcoin-launch\">bitcoin payments</a> with Square POS  and pay zero fees on all bitcoin transactions through 2026. Get near-instant settlement and total protection from chargebacks.', '{}', 'pending', 'woocommerce.com', '2026-01-23 17:04:43', NULL, 0, 'plain', '', 0, 0, 'info');
